import React from 'react';
import { Popover } from 'react-bootstrap';

const CustomPopover = ({ handleColorChange, setSelectedTool }) => {
  const colors = [
    '#FF0000', // Red
    '#00FF00', // Green
    '#0000FF', // Blue
    '#FFFF00', // Yellow
    '#FF00FF', // Magenta
    '#00FFFF', // Cyan
    '#FFA500', // Orange
    '#800080', // Purple
    '#008000', // Dark Green
  ];

  const rows = [];
  const maxColorsPerRow = 3;

  for (let i = 0; i < colors.length; i += maxColorsPerRow) {
    const rowColors = colors.slice(i, i + maxColorsPerRow);
    const row = (
      <div key={i} style={{ display: 'flex' }}>
        {rowColors.map((color, index) => (
          <div
            key={index}
            onClick={() => handleColorChange(color)}
            style={{
              backgroundColor: color,
              borderRadius: '50%',
              width: '24px',
              height: '24px',
              margin: '6px',
              cursor: 'pointer',
            }}
          ></div>
        ))}
      </div>
    );
    rows.push(row);
  }

  return (
    <Popover id="popover-basic">
      <Popover.Body>
        {rows}
        <div
          onClick={() => setSelectedTool("line")}
          style={{ color: "green", fontWeight: "bold", padding: "12px" }}
        >
          <img src="data:image/gif;base64," style={{width:"50px",backgroundColor:"black",height:"5px"}}
           alt="" />
        </div>

        <div
          onClick={() => setSelectedTool("line2")}
          style={{ color: "green", fontWeight: "bold", padding: "12px" }}
        >
          <img src="data:image/gif;base64," style={{width:"50px",backgroundColor:"black",height:"5px"}}
           alt="" />
        </div>

        <div
          onClick={() => setSelectedTool("line3")}
          style={{ color: "green", fontWeight: "bold", padding: "12px" }}
        >
          <img src="data:image/gif;base64," style={{width:"50px",backgroundColor:"black",height:"5px"}}
           alt="" />
        </div>

      </Popover.Body>
    </Popover>
  );
};

export default CustomPopover;
